mapper.update:
UPDATE t_user
user_name=#{userName}, mobile=#{mobile}, password=#{password}, status=#{status},
WHERE user_id=#{userId}

mapper.select:
SELECT * FROM t_user
and `status` = #{status}
and type = #{type}
and mobile LIKE CONCAT("%",#{mobile},"%")
and user_name LIKE CONCAT("%",#{userName},"%")
